import React, { Component } from 'react'

export default class OrderForm extends Component {

    constructor(){
        super();
        this.state = {
            orderData : []
        }
    }
    render() {
        return (
            <div>
                <form>
                    <label>Name</label>
                    <input type="text" ref="txtName" placeholder="enter name" />
                    <label>Price</label>
                    <input type="text" ref="txtPrice" placeholder="enter price" />
                    <label>Name</label>
                    <input type="text" ref="txtName" placeholder="enter name" />
                </form>
            </div>
        )
    }
}
