import "./App.css";
import React, { useState } from "react";

import OrderForm from "./components/OrderForm";
import OrderList from "./components/OrderList";
function App() {
  const orderData = [
    {
      id: 1,
      name: "ashish",
      price: 50,
      description: "surat" 
    }
  ];

const [order, setOrder] = useState(orderData);
const [updateData, setUpdateData] = useState([]);

//add order


const addOrder = (submitData) => {
  if(submitData.id){
    let newData = {
      id: Math.floor(Math.random() * 999),
      name : submitData.name,
      price: submitData.price,
      description : submitData.description
    }
    const updateOrder = [
      ...order,
      newData
    ]
    setOrder(updateOrder);
  }else{
    let idd = submitData.id;
    const result = order.map(x => {
      const item = order.find(({ idd }) => id === x.id);
      return item ? item : x;
    });
    console.log(result);
  }

}
const editHanlder = (id) =>{ 
  setUpdateData(order.find(order => order.id === id));

}
  return (
    <div className="App">
      <OrderForm addOrder={addOrder} updateData={updateData}/>
      <OrderList order={order} editHanlder={editHanlder}/>
    </div>
  );
}

export default App;
