import React, { useState } from "react";
import OrderList from './OrderList';
function OrderForm({addOrder,updateData}) {
    console.log(updateData);
  let [orderData, setorderData] = useState({
    id : updateData.id ? updateData.id : null,  
    name: updateData.name ? updateData.name : "", 
    price: updateData.price ? updateData.price : "",
    description:updateData.description ? updateData.description : "", 
  });



  let handleChange = e => {
    let name = e.target.name;
    let value = e.target.value;
    orderData[name] = value;
    setorderData(orderData);
  };

  let save = e => {
    e.preventDefault();
    addOrder(orderData);

  };

  return (
    <div>
      <fieldset>
        <form method="post" onSubmit={save}>
          Username <input type="text" name="name" value={orderData.name} onChange={handleChange} />
          <br />
          Price
          <input type="text" name="price" onChange={handleChange} />
          <br />
          Description
          <br />
          <textarea
            name="description"
            cols="20"
            rows="5"
            onChange={handleChange}
          ></textarea>
          <br />
          <input type="submit" value="Save" />
        </form>
      </fieldset>
       <hr />

    </div>
  );
}

export default OrderForm;
