import React, { useState,useEffect } from "react";
import OrderList from './OrderList';
function OrderForm({addOrder,updateData}) {


  let [orderData, setorderData] = useState({
    name: "",
    price: "",
    description: ""
  });




  let save = e => {
    e.preventDefault();
    addOrder(orderData);

  };

  return (
    <div>
      <fieldset>
        <form method="post" onSubmit={save}>
          Username <input type="text" name="name" value={orderData.name} onChange={e => setorderData({...orderData,name:e.target.value})} />
          <br />
          Price
          <input type="text" name="price" value={orderData.price} onChange={e => setorderData({...orderData,name:e.target.value})} />
          <br />
          Description
          <br />
          <textarea
            name="description"
            cols="20"
            rows="5"
            value={orderData.description} onChange={e => setorderData({...orderData,name:e.target.value})} 
          ></textarea>
          <br />
          <input type="submit" value="Save" />
        </form>
      </fieldset>
       <hr />

    </div>
  );
}

export default OrderForm;
