import React, { useState } from "react";
import OrderList from './OrderList';
function OrderForm() {
  let [orderData, setorderData] = useState({
    username: "",
    price: "",
    description: ""
  });

  let [arrListOrders, setArrListOrders] = useState([]) ;

  let search = e => {
    e.preventDefault();
    let keyword = e.target.elements.keyword.value;
    console.log("Keyword: " + keyword);
  };

  let handleChange = e => {
    let name = e.target.name;
    let value = e.target.value;
    orderData[name] = value;
    setorderData(orderData);
  };

  let save = e => {
    e.preventDefault();
    console.log(...arrListOrders);
    setArrListOrders([...arrListOrders, orderData]);

  };

  return (
    <div>
      <fieldset>
        <form method="post" onSubmit={save}>
          Username <input type="text" name="username" onChange={handleChange} />
          <br />
          Price
          <input type="text" name="price" onChange={handleChange} />
          <br />
          Description
          <br />
          <textarea
            name="description"
            cols="20"
            rows="5"
            onChange={handleChange}
          ></textarea>
          <br />
          <input type="submit" value="Save" />
        </form>
      </fieldset>
       <hr />
      <OrderList orderList={arrListOrders}/>
    </div>
  );
}

export default OrderForm;
